    public function getSourceContext()
    {
        return new Source("{% extends \"base.html.twig\" %}
{% block body %}
    <div class=\"container\">
        <h1 class=\"mt-4\">Modifier  une recette</h1>

        {% for message in app.flashes('success') %}
            <div class=\"alert alert-success mt-4\">
                {{ message }}
            </div>
        {% endfor %}

        {{ form_start(form) }}
        <div class=\"form-group\">
            {{ form_label(form.name) }}
            {{ form_widget(form.name) }}
            <div class=\"form-error\">
                {{ form_errors(form.name) }}
            </div>

        <div class=\"form-group\">
            {{ form_label(form.time) }}
            {{ form_widget(form.time) }}
            <div class=\"form-error\">
                {{ form_errors(form.time) }}
            </div>
        </div>

        <div class=\"form-group\">
            {{ form_label(form.nbPeople) }}
            {{ form_widget(form.nbPeople) }}
            <div class=\"form-error\">
                {{ form_errors(form.nbPeople) }}
            </div>
        </div>

        <div class=\"form-group\">
            {{ form_label(form.difficulty) }}
            {{ form_widget(form.difficulty) }}
            <div class=\"form-error\">
                {{ form_errors(form.difficulty) }}
            </div>
        </div>

        <div class=\"form-group\">
            {{ form_label(form.description) }}
            {{ form_widget(form.description) }}
            <div class=\"form-error\">
                {{ form_errors(form.description) }}
            </div>
        </div>


        <div class=\"form-group\">
            {{ form_label(form.price) }}
            {{ form_widget(form.price) }}
            <div class=\"form-error\">
                {{ form_errors(form.price) }}
            </div>
        </div>

        <div class=\"form-group mt-4\">
            {{ form_label(form.isFavorite) }}
            {{ form_widget(form.isFavorite) }}
            <div class=\"form-error\">
                {{ form_errors(form.isFavorite) }}
            </div>
        </div>


        {{ form_row(form.ingredients) }}

            <div class=\"form-group\">
                {{ form_row(form.submit, {'label': 'Modifier la recette'}) }}
            </div>
        {{ form_end(form) }}

    </div>
{% endblock %}", "pages/recipe/edit.html.twig", "/Users/axelvair/Documents/Workspace/Symrecipe3/symrecipe/templates/pages/recipe/edit.html.twig");
