        return new Source("{% extends \"base.html.twig\" %}
{% block body %}
    <div class=\"container\">
        <h1 class=\"mt-4\">Modifier  un ingrédient</h1>


        {% for message in app.flashes('success') %}
            <div class=\"alert alert-success mt-4\">
                {{ message }}
            </div>
        {% endfor %}
        {{ form_start(form) }}

        <div class=\"form-group\">
            {{ form_label(form.name) }}
            {{ form_widget(form.name) }}
            <div class=\"form-error\">
                {{ form_errors(form.name) }}
            </div>

        </div>

        <div class=\"form-group\">
            {{ form_label(form.price) }}
            {{ form_widget(form.price) }}
            <div class=\"form-error\">
                {{ form_errors(form.price) }}
            </div>
        </div>

        <div class=\"form-group\">
            {{ form_row(form.submit, {'label': 'Modifier l\\'ingredient'}) }}
        </div>

        {{ form_end(form) }}
    </div>
{% endblock %}", "pages/ingredient/edit.html.twig", "/Users/axelvair/Documents/Workspace/Symrecipe3/symrecipe/templates/pages/ingredient/edit.html.twig");
